import React from 'react'
import { useEffect, useState } from 'react';
export default function Add() {
    const [book, setBook] = useState({
        title:"",
        price:null,
        desc:"",
        cover:""
    });
    const handleChange = (e)=>{
        setBook(prev=>({...prev,[e.target.name]:e.target}))
    }

    return (
        <div className="form">
            <h1>Add New Book</h1>
            <input type="text" onChange={handleChange()} placeholder='title' />
            <input type="text" onChange={handleChange()} placeholder='desc' />
            <input type="text" onChange={handleChange()} placeholder='cover' />
            <input type="number" onChange={handleChange()} placeholder='price' />
        </div>
    )
}
